complete = #{complete}, moral_1 = #{moral1}, moral_2 = #{moral2}, quality_1 = #{quality1}, quality_2 = #{quality2}, quality_3 = #{quality3}, ability_1 = #{ability1}, ability_2 = #{ability2}, ability_3 = #{ability3}, performance_1 = #{performance1}, performance_2 = #{performance2}, performance_3 = #{performance3}, update_at = NOW()
WHERE id = #{id}

mapper.select:
